INSERT INTO hardgaming.games (id, name, shortdesc, users, price, avail, sku, img) VALUES (1, 'ARK: Survival Evolved', 'Ark: Survival Evolved (stylized as ARK) is an action-adventure survival game Where, players must survive roaming dinosaurs, fictional fantasy monsters and other prehistoric animals, natural hazards, and potentially hostile human players.', 10, 1000, 1, 'price_1L59uxGLO8h1ns7mmBOtRS6C', '/img/ark-survival-evolved-logo.jpg');
