create table games
(
    id        int auto_increment
        primary key,
    name      varchar(255)          not null,
    shortdesc mediumtext            not null,
    users     int        default 10 not null,
    price     float      default 0  not null,
    avail     tinyint(1) default 1  not null,
    sku       varchar(255)          not null,
    img       varchar(255)          not null,
    constraint games_id_uindex
        unique (id),
    constraint games_name_uindex
        unique (name),
    constraint games_sku_uindex
        unique (sku)
);

